package com.guessinggame.guessinggame.entity;import java.util.LinkedHashSet;import java.util.Random;import java.util.Set;public class Game {    private User user;    private boolean win;    private boolean lost;    public Game() {        win = false;        lost = false;    }    public boolean isWin() {        return win;    }    public void setWin(boolean win) {        this.win = win;    }    public boolean isLost() {        return lost;    }    public void setLost(boolean lost) {        this.lost = lost;    }    public User getUser() {        return user;    }    public void setUser(User user) {        this.user = user;    }}