package com.guessinggame.guessinggame.model;import javax.validation.constraints.Pattern;import javax.validation.constraints.Size;public class Attempt {    @Size(min = 1, max = 1, message = "There should be one digit number from 0 to 9")    @Pattern(regexp = "[0-9]{1}", message = "There should be a digit number")    private String firstDigit = "";    @Size(min = 1, max = 1, message = "There should be one digit number from 0 to 9")    @Pattern(regexp = "[0-9]{1}", message = "There should be a digit number")    private String secondDigit = "";    @Size(min = 1, max = 1, message = "There should be one digit number from 0 to 9")    @Pattern(regexp = "[0-9]{1}", message = "There should be a digit number")    private String thirdDigit = "";    @Size(min = 1, max = 1, message = "There should be one digit number from 0 to 9")    @Pattern(regexp = "[0-9]{1}", message = "There should be a digit number")    private String fourthDigit = "";    public String getFirstDigit() {        return firstDigit;    }    public void setFirstDigit(String firstDigit) {        this.firstDigit = firstDigit;    }    public String getSecondDigit() {        return secondDigit;    }    public void setSecondDigit(String secondDigit) {        this.secondDigit = secondDigit;    }    public String getThirdDigit() {        return thirdDigit;    }    public void setThirdDigit(String thirdDigit) {        this.thirdDigit = thirdDigit;    }    public String getFourthDigit() {        return fourthDigit;    }    public void setFourthDigit(String fourthDigit) {        this.fourthDigit = fourthDigit;    }    public int[] getLastGuess() {        int[] lastGuess = {                Integer.parseInt(firstDigit),                Integer.parseInt(secondDigit),                Integer.parseInt(thirdDigit),                Integer.parseInt(fourthDigit)        };        return lastGuess;    }    @Override    public String toString() {        return String.valueOf(firstDigit+ "" + secondDigit+ "" + thirdDigit + ""+ fourthDigit);    }}