package com.guessinggame.guessinggame.services;import com.guessinggame.guessinggame.entity.User;import com.guessinggame.guessinggame.repository.UserRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class UserService {    @Autowired    private UserRepository userRepository;    public List<User> findAll() {        return userRepository.findAll();    }    public User getOne(long id) {        return userRepository.getOne(id);    }    public void save(User user){        userRepository.save(user);    }    public void deleteById(long id) {        userRepository.deleteById(id);    }    public User getUserByUsername(String username) {        User userFromDB = null;        for(User user : findAll()) {            if(user.getUsername().equals(username)){                userFromDB = user;            }        }        return userFromDB;    }    public List<User> findAllByMinPlayedGAndGamesWin(Integer played_games){        return userRepository.findAllByMinPlayedGAndGamesWin(played_games);    }}